import React, { useState, useEffect } from 'react';
import './App.css';
import { pricingData } from './data/pricingData';
import { calculatePlanPrice, calculateAddonPrice, calculateTotalPrice } from './utils/calculator';
import Slider from './components/Slider';
import FeatureList from './components/FeatureList';

function App() {
  const [selectedPlan, setSelectedPlan] = useState(pricingData.plans[0]);
  const [planUnits, setPlanUnits] = useState(1);
  const [addonUnits, setAddonUnits] = useState({});
  const [selectedAddons, setSelectedAddons] = useState([]);

  // Initialize addon units
  useEffect(() => {
    const initialAddonUnits = {};
    pricingData.addons.forEach(addon => {
      if (selectedPlan.available_addons.includes(addon.id)) {
        initialAddonUnits[addon.id] = 0;
      }
    });
    setAddonUnits(initialAddonUnits);
  }, [selectedPlan]);

  // Get available addons for selected plan
  const availableAddons = pricingData.addons.filter(addon => 
    selectedPlan.available_addons.includes(addon.id)
  );

  // Calculate prices
  const planPrice = calculatePlanPrice(selectedPlan, planUnits);
  const addonPrices = {};
  let totalAddonPrice = 0;

  availableAddons.forEach(addon => {
    if (addonUnits[addon.id]) {
      addonPrices[addon.id] = calculateAddonPrice(addon, addonUnits[addon.id]);
      totalAddonPrice += addonPrices[addon.id];
    }
  });

  const totalPrice = planPrice + totalAddonPrice;

  const handleAddonUnitChange = (addonId, value) => {
    setAddonUnits(prev => ({
      ...prev,
      [addonId]: value
    }));
  };

  return (
    <div className="app">
      <div className="app-container">
        <header className="app-header">
          <div className="header-content">
            <h1 className="app-title">
              <span className="vendor-name">{pricingData.vendor}</span>
              <span className="title-separator">•</span>
              <span className="title-subtitle">Pricing Calculator</span>
            </h1>
            <p className="app-description">
              Calculate your monthly costs with our dynamic pricing calculator
            </p>
          </div>
        </header>

        <main className="app-main">
          <div className="calculator-grid">
            {/* Left Column - Calculator Controls */}
            <div className="calculator-controls">
              <div className="plan-section">
                <h2 className="section-title">Plan Configuration</h2>

                <div className="plan-info">
                  <h3 className="plan-name">{selectedPlan.label}</h3>
                  <p className="plan-description">{selectedPlan.description}</p>
                </div>

                <Slider
                  label={`${selectedPlan.label} ${selectedPlan.unit}s`}
                  value={planUnits}
                  onChange={setPlanUnits}
                  min={1}
                  max={50}
                  unit={selectedPlan.unit}
                  price={planPrice}
                  description={`$${selectedPlan.unit_price} per ${selectedPlan.unit}`}
                />
              </div>

              {availableAddons.length > 0 && (
                <div className="addons-section">
                  <h2 className="section-title">Add-ons</h2>
                  {availableAddons.map(addon => (
                    <Slider
                      key={addon.id}
                      label={addon.label}
                      value={addonUnits[addon.id] || 0}
                      onChange={(value) => handleAddonUnitChange(addon.id, value)}
                      min={0}
                      max={1000}
                      unit={addon.unit}
                      price={addonPrices[addon.id] || 0}
                      description={addon.description}
                    />
                  ))}
                </div>
              )}
            </div>

            {/* Right Column - Results and Features */}
            <div className="calculator-results">
              <div className="pricing-summary">
                <h2 className="section-title">Pricing Summary</h2>

                <div className="price-breakdown">
                  <div className="price-item">
                    <span className="price-label">{selectedPlan.label} ({planUnits} {selectedPlan.unit}s)</span>
                    <span className="price-value">${planPrice.toFixed(2)}</span>
                  </div>

                  {availableAddons.map(addon => {
                    if (addonUnits[addon.id]) {
                      return (
                        <div key={addon.id} className="price-item">
                          <span className="price-label">{addon.label} ({addonUnits[addon.id]} {addon.unit}s)</span>
                          <span className="price-value">${addonPrices[addon.id].toFixed(2)}</span>
                        </div>
                      );
                    }
                    return null;
                  })}

                  <div className="price-total">
                    <span className="total-label">Total Monthly</span>
                    <span className="total-value">${totalPrice.toFixed(2)}</span>
                  </div>
                </div>

                <div className="cta-section">
                  <button className="cta-button">
                    Get Started
                  </button>
                  <p className="cta-note">No credit card required • Cancel anytime</p>
                </div>
              </div>

              <FeatureList 
                features={selectedPlan.features} 
                title={`${selectedPlan.label} Features`}
              />

              {availableAddons.map(addon => (
                <FeatureList 
                  key={addon.id}
                  features={addon.features} 
                  title={`${addon.label} Features`}
                />
              ))}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
